model_solution:
#include <stdio.h>
#include <string.h>

int main() {
  char alice_msg[101];
  scanf("%s", alice_msg);

  int num_e_alice = strlen(alice_msg) - 2;
  int num_e_bob = num_e_alice * 2;

  printf("h");
  for (int i = 0; i < num_e_bob; i++) {
    printf("e");
  }
  printf("y\n");

  return 0;
}
